import React, {Component} from 'react';

class ContactList extends Component {
	constructor(props){
		super(props);
	}

	handleChange = (event) => {
		let username = event.target.name;
		this.props.updateContactId(username);
	}

	render(){
		let options = []
		for(let i = 0; i < this.props.contacts.length; i++){
			let username = this.props.contacts[i].username
			let currContact = (<li key={username} onClick={this.handleChange}><a name={username} href="#Contact">{username}</a></li>);
			options.push(currContact);
		}
		return (
				<div className="contacts">
					<h3>Contact List</h3>
					<ul>
						{options}
					</ul>
				</div>
			);
	}
}

export default ContactList;
