import React, { Component } from 'react';
import '../style/login.css'
import MSignUp from './ManagerSignUp';
import WSignUp from './WorkerSignUp';
import {send} from '../HelperFunctions';

class Login extends Component {
  constructor() {
    super();
    this.state = {username: '', 
                  password: '',
                  clicked: false,
                  currPage: <Login />
                };
    this.ReadInput = this.ReadInput.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleWSignup = this.handleWSignup.bind(this);
    this.handleMSignup = this.handleMSignup.bind(this);
  }

  ReadInput(event) {
		let {name, value} = event.target;
		this.setState({[name]: value}); 
  }

  handleSubmit(event) {
    event.preventDefault();
    let {username, password} = this.state;
    send("POST", "/signin/", {username, password}, function(err, res){
      if(err) console.log(err);
    });
    console.log('Username: ' + username + '  | Password: ' + password);

  }

  handleWSignup(event) {
    this.setState({
      clicked: true,
      currPage: <WSignUp />,
      });
  }

  handleMSignup(event) {
    this.setState({
      clicked: true,
      currPage: <MSignUp />
      });
  }

  render() {
    let display;
    console.log("clicked: ", this.state.clicked)
    if (this.state.clicked === false){
      return (      <form className="signup_form"  >
      <div className="form_container">
        <label >
          Username <br />
          <input className="form_element" type="text" value={this.state.username} onChange={this.ReadInput} name="username" required/>
        </label>
        <br/>
        <label>
          Password<br />
          <input className="form_element" type="text" value={this.state.password} onChange={this.ReadInput} name="password" required/>
        </label>
        <br />
        <button className="form_btn" onClick={this.handleSubmit}>Login</button>
        <div className="back_forgot_container">
            <button onClick={this.handleWSignup} className="back_btn">Worker Sign Up</button>
            <button onClick={this.handleMSignup} className="back_btn">Manager Sign Up</button>
          </div>
        </div>
      </form>);
    }
    else{	
      display = (this.state.currPage);
    }
    return (
      <div>
        {display}
      </div>
    );
  }
}


export default Login;
