import React, { Component } from 'react';

export default class OrderListItem extends Component {

    render() {
        return (
            <div key={this.props.item.id} class="orderListItems">
                <button type="button" class="removeItemButton" 
                onClick={this.props.handleDelete}>X</button>
                <div 
                    class="item"
                    style={{textDecoration: this.props.item.crossed ? "line-through" : ""}}
                    onClick={this.props.toggleCrossOut}>
                    {this.props.item.name}
                </div>
                <button type="button" class="decrementBtn" 
                onClick={this.props.decrementQty}>-</button>
                <input class="inputQty" value={this.props.item.quantity} onChange={this.props.changeQty} />
                <button type="button" class="incrementBtn" 
                onClick={this.props.incrementQty}>+</button>
            </div>
        );
    }
}
