import React, {Component} from 'react';
import {send} from '../HelperFunctions';
import ContactList from './ContactList';
import Conversation from './Conversation';
import '../style/messaging.css';
import '../style/index.css';
const io = require('socket.io-client');

class Messaging extends Component {
	constructor(){
		super();
		this.state = {
			contact_id : "",
			contacts: [],
			messages: []
		};
		this.socket = io();
	}

	componentDidMount() {
		send("GET", "/api/users/", {}, function(err, res) {
			if(err) console.log(err);
			else{
				this.setState({
					contacts: res
				});
			}
		}.bind(this));
		this.socket.on("messagesUpdate", this.updateMessagesList);
	}

	componentWillUnmount(){
		this.socket.close();
	}

	updateContactId = (newContactId) => {
		this.setState({contact_id: newContactId})
		send("GET", "/api/messages/?recipient=" + newContactId, {}, function(err, res) {
			if(err) console.log(err);
			else{
				this.setState({
					messages: res
				});
			}
		}.bind(this));
	};

	updateMessagesList = (newMessage) => {
		console.log(newMessage.recipient);
		console.log(this.state.contact_id);
		if(newMessage.recipient === this.state.contact_id){
			this.state.messages.push(newMessage);
			this.setState({messages: this.state.messages});
		}
	};

	render(){
		return (
				<div className="messagingContainer">
					<ContactList contacts={this.state.contacts} updateContactId={this.updateContactId} />
					<Conversation socket={this.socket} key={this.state.contact_id} userId={this.state.contact_id} messages={this.state.messages} updateMessages={this.updateMessagesList} />
				</div>
			);
	}
}

export default Messaging;
